mapper.select:
select DATE_FORMAT(t1.date,'%Y-%m-%d') as date, group_name, total from ( select @cdate := date_add(@cdate, INTERVAL -1 DAY) as date from ( select @cdate := date_add(#{con.endDate}, INTERVAL +1 DAY) from x_system ) tmp1 where date_add(@cdate, INTERVAL -1 DAY) >= #{con.startDate} and date_add(@cdate, INTERVAL -1 DAY) <=#{con.endDate} ) t1 left join ( SELECT g.group_name, sum(c.total) total, DATE_FORMAT(c.commit_date, '%Y-%m-%d') AS date FROM `x_commit` c LEFT JOIN x_group_user gu ON c.committer_name = gu.user_name left join x_group g on gu.group_id = g.group_id WHERE 1 = 1
AND c.commit_date >= #{con.startDate}
AND c.commit_date <= #{con.endDate}
group by DATE_FORMAT(c.commit_date,'%Y-%m-%d'),g.group_id ) t2 on t1.date = STR_TO_DATE(t2.date, '%Y-%m-%d') order by date asc
